jQuery( document ).ready(function($) {
  // Taken from Medium article @(https://medium.com/@mariusc23/hide-header-on-scroll-down-show-on-scroll-up-67bbaae9a78c#.yd0v5h41r)
  // Hide Header on on scroll down
  var didScroll;

  var lastScrollTop = 0;
  var delta = 5;
  var navbarHeight = $('nav#block-mgapcdev-mainnavigation').outerHeight();
  $ele = $('nav#block-mgapcdev-mainnavigation');
  // on scroll, let the interval function know the user has scrolled
  $(window).scroll(function(event){
    didScroll = true;
  });
  // run hasScrolled() and reset didScroll status
  setInterval(function() {
    if (didScroll) {
      hasScrolled();
      didScroll = false;
    }
  }, 250);
  function hasScrolled() {
    var st = $(this).scrollTop();
    if (Math.abs(lastScrollTop - st) <= delta) {
      return;
    }
    // If they scrolled down and are past the navbar, add class .nav-up.
    // This is necessary so you never see what is "behind" the navbar.
    if (st > lastScrollTop && st > navbarHeight){
      // Scroll Down
      $ele.removeClass('nav-down').addClass('nav-up');
    } else {
      // Scroll Up
      if(st + $(window).height() < $(document).height()) {
        $ele.removeClass('nav-up').addClass('nav-down');
      }
    }
    lastScrollTop = st;
  }
});
